settings.upsell_custom_css:
:root {
--upsellButtonTextTransform: uppercase;
}
.upsell-cart-overlay, .upsell-cart-riser-open .upsell-cart__form:before {
background-color: rgba(242,237,232,0.5);
}
.upsell-cart-open .upsell-cart-overlay {
opacity: 1;
}
.upsell-cart__close, .upsell-cart__close-riser {
top: 10px;
right: 10px;
}
.upsell-cart__items {
padding-top: 2rem;
}
.upsell-cart__empty-icon {
display: none;
}
.upsell-cart--empty .upsell-cart__empty {
display: flex;
flex-direction: column;
width: 100%;
height: 100%;
justify-content: space-between;
padding: 2rem 2px;
}
.upsell-cart__empty-content { 
max-width: 100%;
width: 100%;
}
.upsell-cart__empty-label {
font-size: 1rem;
text-transform: uppercase;
letter-spacing: 0.03em;
}
.upsell-cart__checkout, .upsell-cart__cta, .upsell-cart__empty-btn {
border-radius: 0;
font-size: 1.4rem !important;
padding: 0.2rem 2rem !important;
min-height: 32px;
line-height: 1.2;
border-width: 1px;
}
.upsell-cart__add-btn {
border-radius: 2px;
padding: 0.2rem 2rem !important;
font-size: 1.1rem !important;
line-height: 1.2 !important;
border-width: 1px;
}
.upsell-cart-open .upsell-cart__footer {
margin: 0 2px;
padding: 1rem 0 2rem;
}
.upsell-cart__riser {
padding: 2rem 2px;
}
.upsell-cart, .upsell-cart, .upsell-cart__riser {
box-shadow: none;
width: 400px;
max-width: 100%;
}
@media (max-width: 1023px) {
.upsell-cart, .upsell-cart, .upsell-cart__riser {
width: 100%;
}
.upsell-cart-open .upsell-cart__footer {
padding: 1rem 0 2rem;
}
.upsell-cart__image {
	flex: 0 0 100px;
}
}
@media (max-width: 768px) {
	.upsell-cart__image {
		flex: 0 0 100px;
	}
}
.upsell-cart__item-row {
padding: 0 2rem;
margin-bottom: 0.5rem;
}
.upsell-cart__checkout-wrapper, .upsell-shipping {
margin-top: 1rem;
}
.upsell-cart__subtotal-label, .upsell-cart__subtotal {
font-size: 1.1rem;
font-weight: 400;
letter-spacing: 0.03em;
}
.upsell-cart__image {
	align-self: flex-start;
}
.upsell-cart__image img, a.upsell-cart__riser-header-image img {
width: 100%;
aspect-ratio: 1 / 1.2;
object-fit: cover;
}
.upsell-cart__item {
padding: 1rem 0;
margin: 0 2rem;
}
.upsell-cart__item-details {
justify-content: space-between;
}
.upsell-cart__item-title, .upsell-shipping__text, .upsell-shipping__text span, .upsell-shipping__full-text, .upsell-shipping__full-text span {
align-self: unset;
text-transform: uppercase;
letter-spacing: 0.03em;
}
.upsell-cart__quantity-input, .upsell-cart__quantity-adjust, .upsell-cart__item--variants, .upsell-shipping__text, .upsell-shipping__text span, .upsell-cart__add-note, .upsell-shipping__full-text, .upsell-shipping__full-text span {
font-size: 0.9rem !important;
}
.upsell-shipping__text span, .upsell-shipping__text, .upsell-shipping__full-text, .upsell-shipping__full-text span {
color: #777F6F;
}
.upsell-cart__item-name,
.upsell-cart__price, .upsell-cart__riser-title--product [data-product-title], .upsell-cart__riser-title:not(.upsell-cart__riser-title--product) {
font-size: 1rem;
letter-spacing: 0.03em;
text-transform: uppercase;
font-weight: 400;
}
.upsell-cart__upsell {
padding: 0 1rem 1rem;
}
.upsell-cart__slider, .upsell-cart__upsell-products {
padding-top: 0px;
margin-bottom: 0px;
}
.upsell-cart .btn:active, .upsell-cart .btn:hover, .upsell-cart .button:active, .upsell-cart .button:hover, .upsell-cart__riser .btn:hover, .upsell-cart__riser .button:hover {
background-color: #B1974E;
}
.upsell-cart__upsell-label, .upsell-cart__variant-label, .upsell-cart__slider-arrow[data-action=prev] {
display: none;
}
.upsell-cart__slider {
border: none;
padding: 0;
}
.upsell-cart__slider-arrow[data-action=next] {
right: -2px;
}
.upsell-cart__slider-arrow:hover {
background: white !important;
border-color: #f2ede8;
color: #b1974e !important;
}
.upsell-cart__image--small {
flex: 0 0 100px;
}
.upsell-cart__upsell .upsell-cart__item--slider {
justify-content: flex-start;
margin: 0 1rem;
}
.upsell-cart__item--slider.upsell-cart__item--slider {
padding-left: 0px;
}
.upsell-cart__riser.upsell-cart__riser--open {
border-top: solid 1px #F2EDE8;
}
.upsell-cart__riser-header, .upsell-cart__riser-main {
padding: 0 2rem;
margin: 0 0 1rem;
border-bottom: none;
}
label[data-upsell-variant-label] {
padding: 0.25rem 0.75rem;
font-size: 1.2rem !important;
margin: 0;
min-width: unset;
box-shadow: none;
border: solid 1px white;
}
input[data-upsell-variant][type=radio]:checked+label {
border: solid 1px var(--upsellDrawerBorder);
box-shadow: none;
background: transparent;
color: #5b5b5b;
}
label[data-upsell-variant-label]:hover {
border: solid 1px var(--upsellDrawerBorder);
box-shadow: none;
}
